<!-- <script type="text/javascript" src="http://code.jquery.com/jquery-1.4.4.min.js"></script> --><script type="text/javascript" src="../jquery.js"></script><div><span id="court_stat_msg" style="color:red;"></span></div><br/><form id="cout_update" name="cout_update">  <!-- used 1 as placeholder -->  <input type="hidden" id="prchID" name="prchID" value="1"/>  <input type="hidden" id="formAction" name="formAction" value="changeCourt"/>  <div id="prchCourtName"><b>Currently Selected Court:</b> </div>  <br/>  <div class="field required-field">  	<div class="label-wrapper"><label for="input-ticket-country">County Ticket Was Issued In:</label></div>  	<?php include_once "inc-county.php" ;?>     </div>     <br/>  <div class="field required-field" id="input-county-choose-wrapper">       <div class="label-wrapper"><label for="input-county-choose">Name Of Court:</label></div>        <select id="input-county-choose" name="input-county-choose">       </select>     </div>  <br/>  <input type="button" name="change" value="Change" onclick="change_court();" style="margin:5px 0 0 40px;"></form><script type="text/javascript">	function change_court(){		var serializedData = jQuery("#cout_update").serialize();		var plugins_url = "ajax_update_data.php";		jQuery.ajax({			url: plugins_url,			type: "post",			data: serializedData,			success: function(data){				jQuery("#court_stat_msg").text("Court Changed Successfully to " + data);				jQuery("#prchCourtName").html("<b>Currently Selected Court:</b> " + data);			},			error:function(){				alert("failure");			}		});	}jQuery(document).ready(function () {  if ($("#input-ticket-country option:selected").val() != '') {    $("#input-county-choose-wrapper").show();    $('#input-county-choose').empty();    $("#input-county-choose").append('<option value = "">---Choose Court---</option>');    $('#do-not-know-data').val(null);    var dataString = $("#cout_update").serialize();    $.ajax ({        type: "POST",        url: "inc-courts-json.php",        data: dataString,        cache: false,        success: function (dataJson) {            try {                data = $.parseJSON(dataJson);            } catch (e) {                console.log(e);                console.log(dataJson);                alert(e);            }            $.each(data, function (index, value) {                $("#input-county-choose").append('<option value = "' + value.courtName + '">' + value.courtName + '</option>');            });        },        error: function (request, status, error) {            alert(request.responseText);        }    });  } else {      $("#input-county-choose-wrapper").hide();      $('#court-exept-efile').hide();  }  //County Change  $("#input-ticket-country").change(function () {    $('#court-exept-efile').hide();    if ($("#input-ticket-country option:selected").val() != '') {        $("#input-county-choose-wrapper").show(500);        $('#input-county-choose').empty();        $("#input-county-choose").append('<option value = "">---Choose Court---</option>');        $('#do-not-know-data').val(null);    } else {        $("#input-county-choose-wrapper").hide(500);    }    var dataString = $("#cout_update").serialize();    $.ajax ({      type: "POST",      url: "inc-courts-json.php",      data: dataString,      cache: false,      success: function (dataJson) {          try {              data = $.parseJSON(dataJson);          } catch (e) {              console.log(e);              console.log(dataJson);              alert(e);          }          $.each(data, function (index, value) {              $("#input-county-choose").append('<option value = "' + value.courtName + '">' + value.courtName + '</option>');          });      },      error: function (request, status, error) {          alert(request.responseText);      }    });  });});</script>